mapper.select:
select f.fct_cd, f.fct_nm, f.line_no, f.prc_cd, f.fct_std, f.comp_cd,f.fct_model, f.use_purp, f.in_date, f.purch_cost, f.chk_prod, f.chk_prod_unit, f.fct_phs, f.fct_img, f.upload_path, a.prc_nm as  prcNm
from fct_info f join adm_prc_mng a 
    on f.prc_cd = a.prc_cd
where 1=1

		and  substr(f.fct_cd,1,11) = #{checkPrcCd}



			AND f.in_date <= #{fixTo}




			AND f.in_date >= #{fixFrom}

mapper.update:
	update fct_info set 
					 FCT_NM= #{fctNm}
					,PRC_CD= #{prcCd}
					,FCT_STD= #{fctStd}
					,FCT_MODEL= #{fctModel}
					,IN_DATE= #{inDate}
					,PURCH_COST= #{purchCost}
					,CHK_PROD= #{chkProd}
                    ,CHK_PROD_UNIT = #{chkProdUnit}
					,FCT_IMG= #{fctImg}
					,UPLOAD_PATH= #{uploadPath}
	where fct_cd= #{fctCd}
